import { useEffect, useState } from "react";
import axios from "axios";

export default function Articles() {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8000/api/articles/")
      .then(res => setArticles(res.data))
      .catch(err => console.error("Gagal ambil artikel:", err));
  }, []);

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Daftar Artikel</h1>
      <div className="grid grid-cols-1 gap-4">
        {articles.map((item, idx) => (
          <div key={idx} className="p-4 bg-white rounded shadow">
            <h2 className="text-xl font-semibold">{item.judul}</h2>
            <p className="text-gray-600">{item.isi}</p>
            {item.gambar && (
              <img
                src={`http://localhost:8000${item.gambar}`}
                alt={item.judul}
                className="mt-2 max-h-48 object-cover rounded"
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
