// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [articles, setArticles] = useState([]);
  const [videos, setVideos] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('access');
        const articleRes = await axios.get('http://localhost:8000/api/articles/', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const videoRes = await axios.get('http://localhost:8000/api/videos/', {
          headers: { Authorization: `Bearer ${token}` }
        });

        setArticles(articleRes.data);
        setVideos(videoRes.data);
      } catch (err) {
        setError('Gagal memuat data. Pastikan Anda sudah login.');
      }
    };

    fetchData();
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-3xl font-bold mb-2">Dashboard</h1>
      <p className="text-gray-700 mb-6">
        <strong>CYCO</strong> adalah platform pembelajaran digital berbasis konten artikel dan video yang dirancang untuk membantu pengguna memahami berbagai topik secara interaktif.
      </p>

      <div className="flex gap-4 mb-6">
        <Link to="/articles" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Artikel</Link>
        <Link to="/videos" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Video</Link>
        <Link to="/profile" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Profil</Link>
        <Link to="/upload-article" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">Upload Artikel</Link>
        <Link to="/upload-video" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">Upload Video</Link>
      </div>

      {error && <p className="text-red-600 mb-4">{error}</p>}

      <section className="mb-8">
        <h2 className="text-xl font-semibold mb-2">Artikel</h2>
        {articles.length > 0 ? (
          <ul className="space-y-2">
            {articles.map((article) => (
              <li key={article.id} className="p-4 border rounded shadow">
                <h3 className="text-lg font-bold">{article.title}</h3>
                <p className="text-sm text-gray-600">{article.content}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>Tidak ada artikel.</p>
        )}
      </section>

      <section>
        <h2 className="text-xl font-semibold mb-2">Video</h2>
        {videos.length > 0 ? (
          <ul className="space-y-2">
            {videos.map((video) => (
              <li key={video.id} className="p-4 border rounded shadow">
                <h3 className="text-lg font-bold">{video.title}</h3>
                <p className="text-sm text-gray-600">{video.description}</p>
                <a href={video.video_url} className="text-blue-600 underline" target="_blank" rel="noopener noreferrer">Lihat Video</a>
              </li>
            ))}
          </ul>
        ) : (
          <p>Tidak ada video.</p>
        )}
      </section>
    </div>
  );
};

export default Dashboard;
