import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UploadVideo = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('access');

    if (!token) {
      setMessage('Anda harus login terlebih dahulu.');
      return;
    }

    try {
      await axios.post(
        'http://localhost:8000/api/videos/',
        { title, description, video_url: videoUrl },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage('Video berhasil diunggah!');
      setTitle('');
      setDescription('');
      setVideoUrl('');
      setTimeout(() => navigate('/videos'), 1500); // Arahkan setelah sukses
    } catch (err) {
        console.error(err);
        if (err.response && err.response.data) {
          setMessage('Gagal mengunggah video: ' + JSON.stringify(err.response.data));
        } else {
          setMessage('Gagal mengunggah video. Terjadi kesalahan tak terduga.');
        }
      }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow-md rounded">
      <h2 className="text-2xl font-bold mb-4">Upload Video</h2>

      {message && <p className="mb-4 text-sm text-center text-blue-600">{message}</p>}

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block font-semibold">Judul</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block font-semibold">Deskripsi</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full px-3 py-2 border rounded"
            rows="4"
            required
          ></textarea>
        </div>

        <div>
          <label className="block font-semibold">URL Video (YouTube, Vimeo, dll)</label>
          <input
            type="url"
            value={videoUrl}
            onChange={(e) => setVideoUrl(e.target.value)}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
        >
          Upload
        </button>
      </form>
    </div>
  );
};

export default UploadVideo;
